
/* 1) Fazer o backup da tabela de controle_notas*/ create table bck_notas select * from controle_notas; /* 2) Apagar todos os registros da tabela de controle_notas*/ delete from controle_notas; /* 3) Subir o backup das notas*/ insert into controle_notas (Rm,Nome,Nota1,Nota,Data_nasc) select Rm,Nome,Nota1,Nota,Data_nasc from bck_notas; /* 4) Apagar as notas cuja a média seja menor 6,0*/ delete from controle_notas where (Nota1+Nota)/2 <6.0; /* 5) Restaurar todos as notas cuja a média seja menor que 6,0*/ insert into controle_notas (Rm,Nome,Nota1,Nota,Data_nasc) select Rm,Nome,Nota1,Nota,Data_nasc from bck_notas where (Nota1+Nota)/2 <6.0; /* 6) Apagar somente os Alunos cujo o nome inicia por "A"*/ delete from controle_notas where (Nome) like "a%"; /* 7) Apagar somente os Alunos cujo o nome inicia po "P"*/ delete from controle_notas where (Nome) like "p%"; /* 8) Subir o Backup dos alunos cuja o nome iniciam o nome com "A" e "P"*/ insert into controle_notas (Rm,Nome,Nota1,Nota,Data_nasc) select Rm,Nome,Nota1,Nota,Data_nasc from bck_notas where (Nome) like "a%" or "p%"; select * from controle_notas order by Rm asc; select * from controle_notas;